INSERT INTO public.books(
	name, resume, year, author, editorial, price, stock, "createdAt", "updatedAt")
	VALUES ('Extraterrestre' , 'En octubre de 2017, científicos del observatorio Haleakala, en Hawái, detectaron un objeto interestelar en movimiento próximo a la Tierra. Primero fue catalogado de cometa, pero esta hipótesis fue descartada. Llamado Oumuamua, «mensajero» o «explorador» en hawaiano, finalmente fue catalogado como asteroide, si bien su forma y su comportamiento diferían del resto de los asteroides y cometas de nuestro sistema solar. Cuanto más lo observaban los científicos, más extraño les parecía. Cuando Avi Loeb, catedrático de Astronomía de Harvard, sugirió que Oumuamua era la evidencia de vida y tecnología extraterrestre en el espacio, se desató el que probablemente sea el debate científico más relevante en décadas. En este libro, Loeb presenta por primera vez su teoría al gran público y nos ofrece un fasci- nante viaje por el universo desde los orígenes del tiempo, del espacio y de la vida.', 2021, 'Avi Loeb', 'Planeta'
, 4500, 10, current_timestamp, current_timestamp);


INSERT INTO public.books(
	name, resume, year, author, editorial, price, stock, "createdAt", "updatedAt")
	VALUES ('Riusplay y el Hechizo de la Luna Llena' , 'Una noche de luna llena, mientras Rius entrena con Invictor para convertirse en un valiente caballero, es atacado por una manada de hombres lobo. íLa maldición de la luna llena ha cobrado a su primera y más importante víctima! Y la única manera de salvar su vida es haciendo un viaje a los confines de la tierra, acompañado por Mike, Trollino y Timba, para tener los ingredientes de la porción mágica que necesita.', 2021, 'Riusplay', 'Altea'
, 23330, 100, current_timestamp, current_timestamp);

